import { useState } from "react";

function Nabaso2(){

    const [namee,setName]=useState("");
    const[phonee,setPhone]=useState("");
    const [submitedData,setSubmittedData]=useState([]);

    const onFormSubmit=(event)=>{
        // event.preventDefault();
        setSubmittedData([...submitedData,{namee,phonee}]);
        setName('');
        setPhone('');
        
    }




return(
    <>

    <form onSubmit={onFormSubmit}>
    Name <input type="text" name="name" id="id_name" value={namee} onChange={(e)=>setName(e.target.value)}/>
    <br />
    
    Phone <input type="number" name="phone" id="id_phone" value={phonee} onChange={(e)=>setPhone(e.target.value)} />
    <br />
    <button type="submit">Submit</button>
    </form>

   

   {submitedData.map((element)=>(<li>Name  {element.namee} Phoone {element.phonee} </li>))}
    
    </>
);


}

export default Nabaso2;
