import { useState } from "react";


function Nabasol(){

    const [isName,setIsName]= useState(['']);
    const [isNumber,setIsNumber]= useState(['']);
    const [isEmail,setIsEmail]= useState(['']);

    function handleOnChange(e){
        setIsName(e.target.value);
        setIsNumber(e.target.value);
        setIsEmail(e.target.value);

    }
return(
    <>

    <form onSubmit={handleSubmit}>

        <p> {isName}</p>

    <label htmlFor="name">Name</label><input type="text" id="idText" value={isEmail} onChange={handleOnChange}/> 
    <br />
    <label htmlFor="phone">Number</label><input type="number" name="number" id="idPhone" value={isNumber}onChange={handleOnChange} />
    <br />
    <label htmlFor="Email">Email</label><input type="email" name="email" id="idEmail"  value={isEmail} onChange={handleOnChange}/>
    
    <button type="submit">Submit</button>
    </form>
    
    

    </>
);


}

export default Nabasol;
