import { useState } from "react";

function Inkoop() {
    const [number, setNumber] = useState('');

    // Convert number to numeric type
    const num = Number(number);

    // Function to determine if a number is even or odd
    const isEven = (n) => n % 2 === 0;

    return (
        <>
            <p>This is Inkoop assignment</p>
            <input
                type="number"
                value={number}
                onChange={(e) => setNumber(e.target.value)}
            />

            {/* Display if the input number is even or odd */}
            {number !== '' && (
                <p>{num} is {isEven(num) ? "Even" : "Odd"}</p>
            )}

            {/* Display the next three numbers and their even/odd status */}
            {number !== '' && (
                <div>
                    <p>{num + 1} is {isEven(num + 1) ? "Even" : "Odd"}</p>
                    <p>{num + 2} is {isEven(num + 2) ? "Even" : "Odd"}</p>
                    <p>{num + 3} is {isEven(num + 3) ? "Even" : "Odd"}</p>
                </div>
            )}
        </>
    );
}

export default Inkoop;
